import { Component, EventEmitter, Inject, Input, Output } from '@angular/core';
import { WINDOW } from '@services/window.service';
import { MatLegacyDialog as MatDialog } from '@angular/material/legacy-dialog';
import { ShareExploreDialogComponent } from '../share-explore-dialog/share-explore-dialog.component';
import { SharePlanDialogComponent } from '../../home/share-plan-dialog/share-plan-dialog.component';
import { FeatureService } from '../../features/feature.service';
import { ActivatedRoute } from '@angular/router';
import { map, of } from 'rxjs';
import { PlanStateService } from '@services';
import { canViewCollaborators } from '../../plan/permissions';

export interface Breadcrumb {
  name: string;
  path?: string;
}

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.scss'],
})
export class NavBarComponent {
  @Input() breadcrumbs: Breadcrumb[] = [];
  @Input() area: 'SCENARIOS' | 'EXPLORE' | 'SCENARIO' = 'EXPLORE';
  @Output() goBack = new EventEmitter<void>();

  hasSharePlanFeatureFlag =
    this.featureService.isFeatureEnabled('show_share_modal');

  canSharePlan$ =
    this.route.snapshot?.params && this.route.snapshot?.params['id']
      ? this.planStateService
          .getPlan(this.route.snapshot.params['id'])
          .pipe(map((plan) => canViewCollaborators(plan)))
      : of(false);

  constructor(
    @Inject(WINDOW) private window: Window,
    private dialog: MatDialog,
    private featureService: FeatureService,
    private route: ActivatedRoute,
    private planStateService: PlanStateService
  ) {}

  print() {
    this.window.print();
  }

  share() {
    this.dialog.open(ShareExploreDialogComponent, { restoreFocus: false });
  }

  sharePlan() {
    this.dialog.open(SharePlanDialogComponent, {
      data: {
        planningAreaName: '"' + this.breadcrumbs[0].name + '"',
        planningAreaId: this.route.snapshot.params['id'],
      },
      restoreFocus: false,
      panelClass: 'no-padding-dialog',
    });
  }
}

export interface ChartData {
  label: string;
  measurement: string;
  key: string;
  values: number[];
  metric_layer: string;
}

export const DEFAULT_COLORMAP = 'turbo';
export const NONE_COLORMAP = 'none';

export interface ColormapConfig {
  name?: string;
  values?: ColormapValue[];
}

export interface ColormapValue {
  name?: string;
  percentile?: number;
  rgb?: string;
}

export interface Legend {
  colors?: string[];
  labels?: string[];
  minMaxValues?: number[];
  secondaryLabels?: string[];
}

/** Convert a colormap to a legend object. */
export function colormapConfigToLegend(
  colormap: ColormapConfig,
  minMaxValues?: number[]
): Legend | undefined {
  const sortedValues = colormap.values?.sort((valueA, valueB) => {
    if (valueA?.percentile != undefined && valueB?.percentile != undefined) {
      return valueA.percentile - valueB.percentile;
    }
    return 0;
  });
  return {
    colors: sortedValues?.map((value) => (!!value.rgb ? value.rgb : '')),
    labels: sortedValues?.map((value) => (!!value.name ? value.name : '')),
    minMaxValues: minMaxValues,
  };
}

