import { Component, Input, OnInit } from '@angular/core';
import { MatLegacySnackBar as MatSnackBar } from '@angular/material/legacy-snack-bar';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../../../services';
import { interval, take } from 'rxjs';
import { Plan, Scenario } from 'src/app/types';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';
import { POLLING_INTERVAL } from '../../plan-helpers';
import {
  MatLegacyDialog as MatDialog,
  MatLegacyDialogRef as MatDialogRef,
} from '@angular/material/legacy-dialog';
import { DeleteDialogComponent } from '../../../delete-dialog/delete-dialog.component';
import { canAddScenario } from '../../../plan/permissions';
import {
  SNACK_BOTTOM_NOTICE_CONFIG,
  SNACK_ERROR_CONFIG,
  SNACK_NOTICE_CONFIG,
} from '../../../shared/constants';

import { ScenarioService } from '@services';
import { MatTab } from '@angular/material/tabs';

export interface ScenarioRow extends Scenario {
  selected?: boolean;
}

@UntilDestroy()
@Component({
  selector: 'app-saved-scenarios',
  templateUrl: './saved-scenarios.component.html',
  styleUrls: ['./saved-scenarios.component.scss'],
})
export class SavedScenariosComponent implements OnInit {
  @Input() plan: Plan | null = null;
  user$ = this.authService.loggedInUser$;

  highlightedScenarioRow: ScenarioRow | null = null;
  loading = true;
  showOnlyMyScenarios: boolean = false;
  activeScenarios: ScenarioRow[] = [];
  archivedScenarios: ScenarioRow[] = [];
  scenariosForUser: ScenarioRow[] = [];
  selectedTabIndex = 0;
  totalScenarios = 0;

  constructor(
    private route: ActivatedRoute,
    private authService: AuthService,
    private router: Router,
    private snackbar: MatSnackBar,
    private dialog: MatDialog,
    private scenarioService: ScenarioService
  ) {}

  ngOnInit(): void {
    this.fetchScenarios();
    this.pollForChanges();
  }

  private pollForChanges() {
    // we might want to check if any scenario is still pending in order to poll
    interval(POLLING_INTERVAL)
      .pipe(untilDestroyed(this))
      .subscribe(() => this.fetchScenarios());
  }

  fetchScenarios(): void {
    this.scenarioService
      .getScenariosForPlan(this.plan?.id!)
      .pipe(take(1))
      .subscribe((scenarios) => {
        this.totalScenarios = scenarios.length;

        this.scenariosForUser = this.showOnlyMyScenarios
          ? scenarios.filter((s) => s.user === this.user$.value?.id)
          : scenarios;
        this.activeScenarios = this.scenariosForUser.filter(
          (s) => s.status === 'ACTIVE'
        );
        this.archivedScenarios = this.scenariosForUser.filter(
          (s) => s.status === 'ARCHIVED'
        );
        this.loading = false;
      });
  }

  get canAddScenarioForPlan(): boolean {
    if (!this.plan) {
      return false;
    }
    return canAddScenario(this.plan);
  }

  get showArchiveScenario() {
    if (!this.plan) {
      return false;
    }
    // Users that can add scenarios can potentially archive them.
    // Users that cannot add scenarios can never archive/restore.
    return this.plan.permissions.includes('add_scenario');
  }

  get canArchiveScenario() {
    if (!this.plan || !this.highlightedScenarioRow) {
      return false;
    }
    const user = this.authService.currentUser();
    return (
      user?.id === this.plan.user ||
      user?.id == this.highlightedScenarioRow?.user
    );
  }

  openConfig(configId?: number): void {
    if (!configId) {
      this.router.navigate(['config', ''], {
        relativeTo: this.route,
      });
    } else {
      this.router.navigate(['config', configId], { relativeTo: this.route });
    }
  }

  viewScenario(): void {
    this.router.navigate(['config', this.highlightedScenarioRow?.id], {
      relativeTo: this.route,
    });
  }

  confirmDeleteScenario(): void {
    const dialogRef: MatDialogRef<DeleteDialogComponent> = this.dialog.open(
      DeleteDialogComponent,
      {
        data: {
          name: '"' + this.highlightedScenarioRow?.name + '"',
        },
      }
    );
    dialogRef
      .afterClosed()
      .pipe(take(1))
      .subscribe((confirmed) => {
        if (confirmed) {
          this.deleteScenario([this.highlightedScenarioRow?.id!]);
        }
      });
  }

  private deleteScenario(ids: string[]) {
    this.scenarioService.deleteScenarios(ids).subscribe({
      next: (deletedIds) => {
        this.snackbar.open(
          `Deleted scenario${deletedIds.length > 1 ? 's' : ''}`,
          'Dismiss',
          SNACK_NOTICE_CONFIG
        );
        this.fetchScenarios();
      },
      error: (err) => {
        this.snackbar.open(`Error: ${err}`, 'Dismiss', SNACK_ERROR_CONFIG);
      },
    });
  }

  highlightScenario(row: ScenarioRow): void {
    this.highlightedScenarioRow = row;
  }

  toggleScenarioStatus(archive: boolean) {
    const id = this.highlightedScenarioRow?.id;

    if (id) {
      this.scenarioService.toggleScenarioStatus(Number(id), archive).subscribe({
        next: () => {
          this.snackbar.open(
            `"${this.highlightedScenarioRow?.name}" has been ${
              archive ? 'archived' : 'restored'
            }`,
            'Dismiss',
            SNACK_BOTTOM_NOTICE_CONFIG
          );
          this.highlightedScenarioRow = null;
          this.fetchScenarios();
        },
        error: (err) => {
          this.snackbar.open(
            `Error: ${err.error.error}`,
            'Dismiss',
            SNACK_ERROR_CONFIG
          );
        },
      });
    }
  }

  tabChange(data: { index: number; tab: MatTab }) {
    this.selectedTabIndex = data.index;
    // reset selected row when changing tabs.
    this.highlightedScenarioRow = null;
  }
}

import { Component, Input, OnChanges, OnInit } from '@angular/core';
import { Plan, Region, User } from '../../../types';
import { NOTE_SAVE_INTERVAL } from '../../plan-helpers';
import { filter, interval, switchMap, tap } from 'rxjs';
import { PlanService } from 'src/app/services';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';

export interface SummaryInput {
  id?: number;
  type: string;
  name: string;
  owner: string;
  region: Region;
  area: GeoJSON.GeoJSON;
  status?: string;
  createdTime?: Date;
  scenarios?: number;
  configs?: number;
  lastUpdated: Date;
  acres: number;
  notes?: string;
}

// todo: move this to shared types
export enum ConditionName {
  GOOD = 'Good',
  LEANING_GOOD = 'Leaning good',
  NEUTRAL = 'Neutral',
  LEANING_POOR = 'Leaning poor',
  POOR = 'Poor',
}

export const conditionScoreColorMap: Record<ConditionName, string> = {
  [ConditionName.GOOD]: '#010108',
  [ConditionName.LEANING_GOOD]: '#4c1761',
  [ConditionName.NEUTRAL]: '#b1354c',
  [ConditionName.LEANING_POOR]: '#F4511e',
  [ConditionName.POOR]: '#fdd853',
};

@UntilDestroy()
@Component({
  selector: 'app-summary-panel',
  templateUrl: './summary-panel.component.html',
  styleUrls: ['./summary-panel.component.scss'],
})
export class SummaryPanelComponent implements OnInit, OnChanges {
  @Input() plan: Plan | null = null;
  @Input() owner: User | null = null;

  summaryInput: SummaryInput | null = null;
  conditionScore: ConditionName = ConditionName.POOR;
  futureConditionScore: ConditionName = ConditionName.LEANING_GOOD;
  conditionScoreColorMap = conditionScoreColorMap;
  notes: string = '';

  constructor(private planService: PlanService) {}

  ngOnInit(): void {
    this.notes = this.plan?.notes ? this.plan?.notes : '';
    this.autoSaveNotes();
  }

  ngOnChanges(): void {
    if (!!this.plan) {
      this.summaryInput = {
        id: this.plan!.id,
        type: 'Plan',
        name: this.plan!.name,
        owner: this.owner?.firstName
          ? this.owner?.firstName + ' ' + this.owner?.lastName
          : this.owner?.username ?? 'Guest',
        region: this.plan!.region_name,
        area: this.plan!.geometry!,
        createdTime: new Date(this.plan!.created_at),
        scenarios: this.plan!.scenario_count,
        notes: this.plan!.notes,
        lastUpdated: new Date(this.plan!.latest_updated!),
        acres: Math.round(this.plan.area_acres),
        status: 'In progress',
      };
    }
  }

  autoSaveNotes(): void {
    interval(NOTE_SAVE_INTERVAL)
      .pipe(
        untilDestroyed(this),
        filter((_) => this.plan !== null && this.plan.notes !== this.notes),
        tap((_) => (this.plan!.notes = this.notes)),
        switchMap((_) =>
          this.planService.updatePlanningArea(this.plan!, this.plan!.id)
        )
      )
      .subscribe();
  }
}

