import { Injectable } from '@angular/core';
import { FeatureService } from '../features/feature.service';
import { Region, RegionOption, regions } from '../types';
import { SessionService } from './session.service';
import { map } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class RegionService {
  constructor(
    private features: FeatureService,
    private sessionService: SessionService
  ) {}

  private availableRegions = new Set([
    Region.SIERRA_NEVADA,
    this.features.isFeatureEnabled('show_socal')
      ? Region.SOUTHERN_CALIFORNIA
      : null,
    this.features.isFeatureEnabled('show_centralcoast')
      ? Region.CENTRAL_COAST
      : null,
    this.features.isFeatureEnabled('show_north_cal')
      ? Region.NORTHERN_CALIFORNIA
      : null,
  ]);

  regionOptions: RegionOption[] = regions.map((region) => {
    return {
      type: region,
      name: region,
      available: this.availableRegions.has(region),
    };
  });

  private regionDrawOptions: Record<Region, boolean> = {
    [Region.SIERRA_NEVADA]: true,
    [Region.SOUTHERN_CALIFORNIA]: this.features.isFeatureEnabled('draw_socal'),
    [Region.NORTHERN_CALIFORNIA]:
      this.features.isFeatureEnabled('draw_northcal'),
    [Region.CENTRAL_COAST]: this.features.isFeatureEnabled('draw_centralcoast'),
  };

  drawRegionEnabled$ = this.sessionService.region$
    .asObservable()
    .pipe(map((region) => (region ? this.regionDrawOptions[region] : false)));
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { catchError, Observable, take } from 'rxjs';
import {
  Region,
  regionToString,
  Scenario,
  SCENARIO_STATUS,
  ScenarioConfig,
} from '../types';
import { BackendConstants } from '../backend-constants';
import { CreateScenarioError } from './errors';

@Injectable({
  providedIn: 'root',
})
export class ScenarioService {
  constructor(private http: HttpClient) {}

  /** Fetches the scenarios for a plan from the backend. */
  getScenariosForPlan(planId: number): Observable<Scenario[]> {
    return this.http.get<Scenario[]>(
      BackendConstants.END_POINT.concat(
        `/planning/list_scenarios_for_planning_area/?planning_area=${planId}`
      ),
      {
        withCredentials: true,
      }
    );
  }

  // TODO Add boolean parameter to control if show_results flag is true or false
  /** Fetches a scenario by its id from the backend. */
  getScenario(scenarioId: string): Observable<Scenario> {
    const url = BackendConstants.END_POINT.concat(
      '/planning/get_scenario_by_id/?id=',
      scenarioId
    );
    return this.http.get<Scenario>(url, {
      withCredentials: true,
    });
  }

  /** Creates a scenario in the backend. Returns scenario ID. */
  createScenario(scenarioParameters: any): Observable<any> {
    scenarioParameters['configuration'] = this.convertConfigToScenario(
      scenarioParameters['configuration']
    );
    return this.http
      .post<{
        id: number;
      }>(
        BackendConstants.END_POINT + '/planning/create_scenario/',
        scenarioParameters,
        {
          withCredentials: true,
        }
      )
      .pipe(
        catchError((error) => {
          const message =
            error.error.reason || 'Please change your settings and try again.';
          throw new CreateScenarioError(
            'Your scenario config is invalid. ' + message
          );
        })
      );
  }

  /** Deletes one or more scenarios from the backend. Returns IDs of deleted scenarios. */
  deleteScenarios(scenarioIds: string[]): Observable<string[]> {
    return this.http.post<string[]>(
      BackendConstants.END_POINT.concat('/planning/delete_scenario/'),
      {
        scenario_id: scenarioIds,
      },
      {
        withCredentials: true,
      }
    );
  }

  toggleScenarioStatus(scenarioId: number, archive: boolean) {
    return this.changeScenarioStatus(
      scenarioId,
      archive ? 'ARCHIVED' : 'ACTIVE'
    );
  }

  private changeScenarioStatus(scenarioId: number, status: SCENARIO_STATUS) {
    const url = BackendConstants.END_POINT.concat('/planning/update_scenario/');
    return this.http.patch<number>(
      url,
      {
        id: scenarioId,
        status: status,
      },
      {
        withCredentials: true,
      }
    );
  }

  /** Updates a scenario with new notes. */
  updateScenarioNotes(scenario: Scenario): Observable<number> {
    const url = BackendConstants.END_POINT.concat('/planning/update_scenario/');
    return this.http
      .patch<number>(
        url,
        {
          id: scenario.id,
          notes: scenario.notes,
        },
        {
          withCredentials: true,
        }
      )
      .pipe(take(1));
  }

  downloadCsvData(scenarioId: string): Observable<any> {
    return this.http.get(
      BackendConstants.END_POINT +
        `/planning/get_scenario_download_by_id?id=${scenarioId}`,
      {
        withCredentials: true,
        responseType: 'arraybuffer',
      }
    );
  }

  downloadShapeFiles(scenarioId: string): Observable<any> {
    return this.http.get(
      BackendConstants.END_POINT +
        `/planning/download_shapefile?id=${scenarioId}`,
      {
        withCredentials: true,
        responseType: 'arraybuffer',
      }
    );
  }

  /** Gets Metric Data For Scenario Output Fields */
  getMetricData(metric_paths: any, region: Region): Observable<any> {
    const url = BackendConstants.END_POINT.concat(
      '/conditions/metrics/?region_name=' +
        `${regionToString(region)}` +
        '&metric_paths=' +
        JSON.stringify(metric_paths)
    );
    return this.http.get<any>(url).pipe(take(1));
  }

  private convertConfigToScenario(config: ScenarioConfig): any {
    return {
      question_id: config.treatment_question!.id,
      est_cost: config.est_cost,
      max_budget: config.max_budget,
      min_distance_from_road: config.min_distance_from_road,
      max_slope: config.max_slope,
      max_treatment_area_ratio: config.max_treatment_area_ratio,
      scenario_priorities: config.treatment_question!['scenario_priorities'],
      scenario_output_fields:
        config.treatment_question!['scenario_output_fields_paths']!['metrics'],
      stand_thresholds: config.treatment_question!['stand_thresholds'],
      global_thresholds: config.treatment_question!['global_thresholds'],
      weights: config.treatment_question!['weights'],
      excluded_areas: config.excluded_areas,
      stand_size: config.stand_size,
    };
  }
}

import { BehaviorSubject, interval, Observable } from 'rxjs';
import { Injectable } from '@angular/core';
import { MapConfig, MapViewOptions, Region } from '../types';
import {
  defaultMapConfig,
  defaultMapConfigsDictionary,
  defaultMapViewOptions,
} from '../map/map.helper';

/** How often the user's session should be saved to local storage (in ms). */
const SESSION_SAVE_INTERVAL = 600;

/**
 * The session service keeps track of where the guest or logged-in user left
 * off.
 */
@Injectable({
  providedIn: 'root',
})
export class SessionService {
  /** Components can subscribe to this Observable to perform operations repeatedly
   *  at a set interval (like saving state). */
  readonly sessionInterval$: Observable<number> = interval(
    SESSION_SAVE_INTERVAL
  );

  readonly mapConfigs$ = new BehaviorSubject<Record<
    Region,
    MapConfig[]
  > | null>(defaultMapConfigsDictionary());
  readonly mapViewOptions$ = new BehaviorSubject<MapViewOptions | null>(null);
  readonly region$ = new BehaviorSubject<Region | null>(null);

  constructor() {
    const storedMapConfigs = localStorage.getItem('mapConfigs');
    if (storedMapConfigs && this.validateSavedMapConfigs(storedMapConfigs)) {
      this.mapConfigs$.next(JSON.parse(storedMapConfigs));
    }
    const storedMapViewOptions = localStorage.getItem('mapViewOptions');
    if (
      storedMapViewOptions &&
      this.validateSavedMapViewOptions(storedMapViewOptions)
    ) {
      this.mapViewOptions$.next(JSON.parse(storedMapViewOptions));
    }
    const savedRegion = localStorage.getItem('region');
    if (!!savedRegion) {
      this.setRegion(savedRegion as Region);
    } else {
      this.setRegion(Region.SIERRA_NEVADA);
    }
  }

  /** Emits the map configs and saves them in local storage. */
  setMapConfigs(value: MapConfig[], region?: Region) {
    var regionIndex: Region | null = region || this.region$.getValue();
    if (!regionIndex) {
      regionIndex = Region.SIERRA_NEVADA;
    }
    var mapConf: Record<Region, MapConfig[]> | null =
      this.mapConfigs$.getValue();
    if (mapConf && regionIndex) {
      mapConf![regionIndex] = value;
    }
    this.mapConfigs$.next(mapConf);
    localStorage.setItem('mapConfigs', JSON.stringify(mapConf));
  }

  /** Emits the map view options and saves them in local storage. */
  setMapViewOptions(value: MapViewOptions) {
    localStorage.setItem('mapViewOptions', JSON.stringify(value));
    this.mapViewOptions$.next(value);
  }

  /**
   * Emits the region and saves it in local storage. Saves the default region if local storage's
   * value does not match a region (i.e. enum was changed, but user still has old value stored).
   */
  setRegion(value: Region) {
    if (Object.values(Region).includes(value)) {
      localStorage.setItem('region', value);
      this.region$.next(value);
    } else {
      localStorage.setItem('region', Region.SIERRA_NEVADA);
      this.region$.next(Region.SIERRA_NEVADA);
    }
  }

  /** Validates the map configs loaded from local storage to ensure all required fields
   *  are present. */
  private validateSavedMapConfigs(data: string): boolean {
    const configs: any[] = JSON.parse(data);
    for (var regionConfig of Object.values(configs)) {
      for (var mapConfig of Object.values(regionConfig)) {
        if (!this.instanceOfMapConfig(mapConfig)) {
          console.error('not valid config ' + JSON.stringify(mapConfig));
          return false;
        }
      }
    }
    for (var region of Object.keys(configs)) {
      if (!Object.values(Region).includes(region as unknown as Region)) {
        console.error('Config key not valid Region ' + region);
        return false;
      }
    }

    return true;
  }

  private instanceOfMapConfig(data: any): boolean {
    const mapConfigExample: MapConfig = defaultMapConfig();
    return (
      Object.keys(data).sort().join(',') ===
      Object.keys(mapConfigExample).sort().join(',')
    );
  }

  /** Validates the map view options loaded from local storage to ensure all required fields
   *  are present. */
  private validateSavedMapViewOptions(data: string): boolean {
    const mapViewOptions: MapViewOptions = JSON.parse(data);
    return (
      Object.keys(mapViewOptions).sort().join(',') ===
      Object.keys(defaultMapViewOptions()).sort().join(',')
    );
  }
}

