import {
  Directive,
  Input,
  OnInit,
  TemplateRef,
  ViewContainerRef,
} from '@angular/core';

import { FeatureService } from './feature.service';

/** Directive to show or hide a DOM element based on whether a feature flag is enabled. */
@Directive({
  selector: '[appFeatureFlag]',
})
export class FeatureFlagDirective implements OnInit {
  @Input() appFeatureFlag!: string;
  // if provided as true, will hide the content when the flag is true
  @Input() appFeatureFlagHide = false;

  constructor(
    private featureService: FeatureService,
    private templateRef: TemplateRef<any>,
    private viewContainerRef: ViewContainerRef
  ) {}

  ngOnInit() {
    const isEnabled = this.featureService.isFeatureEnabled(this.appFeatureFlag);
    const shouldShow = this.appFeatureFlagHide ? !isEnabled : isEnabled;
    if (shouldShow) {
      this.viewContainerRef.createEmbeddedView(this.templateRef);
    }
  }
}

import { CdkCellDef } from '@angular/cdk/table';
import { Directive, Input } from '@angular/core';
import {
  MatLegacyCellDef as MatCellDef,
  MatLegacyTableDataSource as MatTableDataSource,
} from '@angular/material/legacy-table';
import { Observable } from 'rxjs';

@Directive({
  // eslint-disable-next-line @angular-eslint/directive-selector
  selector: '[matCellDef]', // same selector as MatCellDef
  providers: [{ provide: CdkCellDef, useExisting: TypeSafeMatCellDef }],
})
// eslint-disable-next-line @angular-eslint/directive-class-suffix
export class TypeSafeMatCellDef<T> extends MatCellDef {
  // leveraging syntactic-sugar syntax when we use *matCellDef
  @Input()
  matCellDefDataSource!: T[] | Observable<T[]> | MatTableDataSource<T>;

  // ngTemplateContextGuard flag to help with the Language Service
  static ngTemplateContextGuard<T>(
    dir: TypeSafeMatCellDef<T>,
    ctx: unknown
  ): ctx is { $implicit: T; index: number } {
    return true;
  }
}

