import { Component, Input } from '@angular/core';
import { MatIconModule } from '@angular/material/icon';
import { CommonModule } from '@angular/common';
import { MatInputModule } from '@angular/material/input';
import { MatFormFieldModule } from '@angular/material/form-field';

@Component({
  selector: 'sg-input',
  standalone: true,
  imports: [MatIconModule, CommonModule, MatInputModule, MatFormFieldModule],
  templateUrl: './input.component.html',
  styleUrl: './input.component.scss',
})
export class InputComponent {
  /**
   * the value of the input field
   */
  @Input() value = '';

  /**
   * The help/error message to be displayed below the input field.
   */
  @Input() supportMessage = '';

  /**
   * Icon displayed on the left side of the input field.
   * If not provided the icon is hidden.
   */
  @Input() leadingIcon = '';

  /**
   * Icon displayed on the right side of the input field.
   * If not provided the icon is hidden.
   */
  @Input() trailingIcon = '';

  /**
   * Determines if the input field is disabled. Affects the styles of the whole component.
   */
  @Input() disabled = false;

  /**
   * Determines if the input field has errors. Affects the styles of the whole component.
   */
  @Input() error = false;

  /**
   * The placeholder displayed on the input field
   */
  @Input() placeholder = '';

  /**
   * Text to be shown on the right side of the input field.
   */
  @Input() suffix = '';
}

import { Component, HostBinding, Input } from '@angular/core';
import { NgIf, NgSwitch } from '@angular/common';

export type StatusChipStatus = 'inProgress' | 'success' | 'failed' | 'running';

/**
 * Status chip to display status inline.
 * Takes optional label or displays the status styled accordingly.
 */
@Component({
  selector: 'sg-status-chip',
  standalone: true,
  imports: [NgIf, NgSwitch],
  templateUrl: './status-chip.component.html',
  styleUrl: './status-chip.component.scss',
})
export class StatusChipComponent {
  /**
   * The status
   */
  @Input() status: StatusChipStatus = 'inProgress';
  /**
   * Optional label
   */
  @Input() label? = '';

  /**
   * @ignore
   */
  readonly defaultTextByStatus: Record<StatusChipStatus, string> = {
    inProgress: 'In Progress',
    success: 'Success',
    failed: 'Failed',
    running: 'Running',
  };

  @HostBinding('class.in-progress')
  get isInProgress() {
    return this.status === 'inProgress';
  }

  @HostBinding('class.success')
  get isSuccess() {
    return this.status === 'success';
  }

  @HostBinding('class.failed')
  get isFailed() {
    return this.status === 'failed';
  }

  @HostBinding('class.running')
  get isRunning() {
    return this.status === 'running';
  }
}
